from time import sleepfrom restapi.seleniumautomation.comman_setup import get_browser, loginsample_data = {    "gstin": "12ABCDE1234A1ZH",    "company_name": "xyz_service",    "company_code": "xyz",    "credit_period": "30",    "contact_person": "contact_Mr.X",    "phone": "8005021704",    "alt_phone": "8005021706",    "email": "xyz@example.com",    "address": "its_here,223423",    "pin": "620015",    "remarks": "everything seems to be ok."}def create_customer(data):    browser = get_browser()    login(browser)    sleep(5)    # finding registration tab    registrationTab = browser.find_element_by_css_selector("i[class='fa fa-registered']")    sleep(2)    # click registration tab    registrationTab.click()    sleep(3)    # find customer tab and click    customerPageElem = browser.find_element_by_css_selector("a[data-url='/page/register-sme-page/']")    browser.execute_script("arguments[0].scrollIntoView();", customerPageElem)    sleep(5)    customerPageElem.click()    sleep(5)    #finding field for creating customer    gstin = browser.find_element_by_css_selector("span[id='select2-is_gst_applicable-container']")    companyName = browser.find_element_by_css_selector("input[name='name']")    companyCode = browser.find_element_by_css_selector("input[name='company_code']")    creditPeriod = browser.find_element_by_css_selector("input[name='credit_period']")    contactPersonName = browser.find_element_by_css_selector("input[name='contact_person_name']")    contactPersonPhone = browser.find_element_by_css_selector("input[name='phone']")    aahoPoc = browser.find_element_by_css_selector("span[id='select2-aaho_poc-container']")    alternatePhone = browser.find_element_by_css_selector("input[name='alternate_phone']")    email = browser.find_element_by_css_selector("input[name='email']")    companyAddress = browser.find_element_by_css_selector("input[name='company_address']")    city = browser.find_element_by_css_selector("span[id='select2-city-container']")    aahoOffice = browser.find_element_by_css_selector("span[id='select2-aaho_office-container']")    pin = browser.find_element_by_css_selector("input[name='pin']")    remarks = browser.find_element_by_css_selector("textarea[name='remarks']")    registerSmeButton = browser.find_element_by_css_selector("button[id='btn-register-sme']")    #filling customer details    gstin.click()    sleep(2)    chhoseGstin = browser.find_element_by_css_selector("ul[id='select2-is_gst_applicable-results'] > li")    chhoseGstin.click()    sleep(5)    gstinInput = browser.find_element_by_css_selector("input[name='gstin']")    if "gstin" in data:        gstinInput.send_keys(data["gstin"])        sleep(2)    if "company_name" in data:        companyName.send_keys(data["company_name"])        sleep(2)    if "company_code" in data:        companyCode.send_keys(data["company_code"])        sleep(2)    if "credit_period" in data:        creditPeriod.send_keys(data["credit_period"])        sleep(2)    if "contact_person" in data:        contactPersonName.send_keys(data["contact_person"])        sleep(2)    if "phone" in data:        contactPersonPhone.send_keys(data["phone"])        sleep(2)    aahoPoc.click()    sleep(2)    choosePoc = browser.find_element_by_css_selector("ul[id='select2-aaho_poc-results'] > li")    choosePoc.click()    sleep(2)    if "alt_phone" in data:        alternatePhone.send_keys(data["alt_phone"])        sleep(2)    if "email" in data:        email.send_keys(data["email"])        sleep(2)    if "address" in data:        companyAddress.send_keys(data["address"])        sleep(2)    city.click()    sleep(2)    chooseCity = browser.find_element_by_css_selector("ul[id='select2-city-results'] > li")    chooseCity.click()    sleep(2)    aahoOffice.click()    sleep(2)    chooseOffice = browser.find_element_by_css_selector("ul[id='select2-aaho_office-results']")    chooseOffice.click()    sleep(2)    if "pin" in data:        pin.send_keys(data["pin"])        sleep(2)    if "remarks" in data:        remarks.send_keys(data["remarks"])        sleep(2)    #click register sme button    registerSmeButton.click()    sleep(10)    browser.quit()